export function handleOrdem(ordem, setOrdem, settipo) {
    // setOrdemDia(false);
    // setOrdemValor(false);
    if (ordem !== "crescente") {
        setOrdem("crescente");

    } else {
        setOrdem("decrescente");
    };

    setRegistros(prevState => {
        const array = [...prevState];
        const arrayOrdenado = array.sort((a, b) => {
            if (ordem === "crescente") {
                return `new Date(b.${tipo}) - new Date(a.${tipo})`;
            } else if (ordem === "decrescente") {
                return new Date(a.date) - new Date(b.date);
            }
        });
        return arrayOrdenado;
    });
};
