import './App.css';
import { listarRegistros } from './registros'
import { useState, useEffect } from 'react';
import { format } from 'date-fns';

function Registro({ id, category, date, description, value, week_day, registro, excluirRegistro, editarRegistro }) {
  return (
    <div>
      <tr key={id} className="table-line">
        <td className="line-items">{new Date(date).toLocaleDateString('pt-BR', { timeZone: 'UTC' })}</td>
        <td className="line-items">{week_day}</td>
        <td className="line-items">{description}</td>
        <td className="line-items">{category}</td>
        <td className="line-items">{"R$ " + (value / 100).toFixed(2)}</td>
        <td className="line-items">
          <img className="edit-icon" onClick={() => { editarRegistro(registro) }} src="./assets/editar.svg" />
          <img className="delete-icon" onClick={() => { excluirRegistro(id) }} src="./assets/excluir.svg" />
        </td>
      </tr>
      <hr width="100%"></hr>
    </div>

  )
}

function App() {
  const [addRegistroVisivel, setAddRegistroVisivel] = useState(false);
  const [filtroVisivel, setFiltroVisivel] = useState(false);
  const [formulario, setFormulario] = useState({ valor: 0, categoria: '', data: '', descricao: '' });
  const [tipoSaida, setTipoSaida] = useState(true);
  const [registros, setRegistros] = useState([]);
  const [editandoRegistro, setEditandoRegistro] = useState(false);
  const [entrada, setEntrada] = useState(0)
  const [saida, setSaida] = useState(0)
  const [saldo, setSaldo] = useState(0)
  const [ordemCrescente, setOrdemCrescente] = useState(true);
  const [minMax, setMinMax] = useState({ min: 0, max: 0 });

  // const dataX = new Date (2017, 3, 12)
  // console.log(format(dataX, 'dd-MMM-yyy'))

  async function handleFiltroSubmit(event) {
    event.preventDefault();
    setRegistros(prevState => {
      const array = [...prevState];
      const arrayFiltrado = array.filter(item => Number(item.value) >= (minMax.min * 100) && Number(item.value) <= (minMax.max * 100));
      return arrayFiltrado;
    })
  }

  function handleChangeFiltro(event) {
    setMinMax({ ...minMax, [event.target.name]: event.target.value });
  }

  // Popular transações ao abrir a aplicação
  useEffect(() => {
    listarRegistros().then((response) => {
      setRegistros(response);
    })
  }, []);

  // Resumo - entradas, saídas e saldo
  useEffect(() => {
    const arrayDeRegistros = [...registros];

    let somaEntrada = 0, somaSaida = 0, valorSaldo = 0;

    for (let i = 0; i < arrayDeRegistros.length; i++) {
      if (arrayDeRegistros[i].type === "credit") {
        somaEntrada = somaEntrada + Number(arrayDeRegistros[i].value)
        // console.log("entrada")
      } else if (arrayDeRegistros[i].type === "debit") {
        somaSaida = somaSaida + Number(arrayDeRegistros[i].value);
        // console.log("saida")
      }
    };

    valorSaldo = somaEntrada - somaSaida;

    setEntrada(somaEntrada);
    setSaida(somaSaida);
    setSaldo(valorSaldo);
    // console.log(saida, entrada, saldo)
  }, [[], registros])

  // FUNÇÃO ENVIO DE FORMULÁRIO
  function handleSubmitAdicionar(event) {
    event.preventDefault();
    // checar event.target, map
    const { valor, categoria, data, descricao } = formulario;
    const semana = ["Domingo", "Segunda", "Terça", "Quarta", "Quinta", "Sexta", "Sábado"];
    const index = new Date(data).getDay();
    const diaDaSemana = semana[index + 1];
    const form = {
      "date": new Date(data),
      "week_day": diaDaSemana,
      "description": descricao,
      "value": valor * 100,
      "category": categoria,
      "type": tipoSaida ? "debit" : "credit"
    };
    console.log(form.date)

    fetch("http://localhost:3333/transactions", {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify(form)
    }).then(response => {
      const responseJSON = response.json().then(novoRegistro => {
        setRegistros([...registros, novoRegistro]);
      })
    })
    setAddRegistroVisivel(false);
    setTipoSaida(true);
  }

  // FUNÇÃO INPUT
  function handleChange(event) {
    setFormulario({ ...formulario, [event.target.name]: event.target.value })
  }

  async function excluirRegistro(id) {
    const deletado = await fetch(`http://localhost:3333/transactions/${id}`, {
      method: "DELETE",
    });
    listarRegistros().then((response) => {
      setRegistros(response);
    });
  };

  async function editarRegistro(registro) {
    if (registro.type === "credit") {
      setTipoSaida(false);
    } else if (registro.type === "debit") {
      setTipoSaida(true);
    }
    setEditandoRegistro(registro);
    setFormulario({
      valor: registro.value,
      categoria: registro.category,
      diaDaSemana: registro.week_day,
      data: registro.date,
      descricao: registro.description,
      id: registro.id
    })
  }

  function handleSubmitEditar(event) {
    console.log("oi")
    event.preventDefault();
    const { valor, categoria, data, descricao } = formulario;
    const semana = ["Domingo", "Segunda", "Terça", "Quarta", "Quinta", "Sexta", "Sábado"];
    const index = new Date(data).getDay();
    const diaDaSemana = semana[index + 1];
    const body = {
      "date": new Date(data),
      "week_day": diaDaSemana,
      "description": descricao,
      "value": valor * 100,
      "category": categoria,
      "type": tipoSaida ? "debit" : "credit"
    };

    fetch(`http://localhost:3333/transactions/${formulario.id}`, {
      headers: {
        'Content-Type': 'application/json'
      },
      method: "PUT",
      body: JSON.stringify(body)
    }).then(response => {
      const responseJSON = response.json().then(novoRegistro => {
        console.log(novoRegistro)
        const array = [...registros];
        let index = array.findIndex(item => item.id = formulario.id);
        array.splice(index, 1, novoRegistro);
        // console.log("array", array)
        setRegistros(array);
      })
    })
    setEditandoRegistro(false);
    setTipoSaida(true);
  }

  function handleCloseModal() {
    setTipoSaida(true);
    setAddRegistroVisivel(false);
    setEditandoRegistro(false);
  }

  function ordenarValor() {
    setRegistros(prevState => {
      const array = [...prevState];
      const arrayOrdenado = array.sort((a, b) => {
        if (ordemCrescente) {
          return Number(a.value) - Number(b.value);
        } else {
          return Number(b.value) - Number(a.value);
        }
      });
      console.log(arrayOrdenado);
      return arrayOrdenado;
    })
    setOrdemCrescente(prevState => {
      if (ordemCrescente) {
        return false;
      } else {
        return true;
      }
    });
  }

  async function funcaoFiltro() {
    if (!filtroVisivel) {
      setFiltroVisivel(true);
    } else {
      setFiltroVisivel(false);
      listarRegistros().then((response) => {
        setRegistros(response);
      })
    }
  }

  // RENDERIZA COMPONENTE APP
  return (
    <div className="App">
      <header className="container-header">
        <img src="./assets/Logo.svg" />
      </header>
      <main>
        <div className="left-side flex-column">
          <button onClick={funcaoFiltro} className="open-filters-button">
            <span className="filtrar-Btn__span">
              <img src="./assets/filtro.svg" />
              Filtrar
            </span>
          </button>

          {
            filtroVisivel && (
              <div className="container-filters">
                <h1>Valor</h1>
                <form onSubmit={handleFiltroSubmit}>
                  <div className="flex-column">
                    <label className="filters-label">Min</label>
                    <input
                      className="filters-input"
                      name="min"
                      id="min-value"
                      type="number"
                      onChange={handleChangeFiltro}
                    ></input>
                  </div>
                  <div className="flex-column">
                    <label className="filters-label">Max</label>
                    <input
                      className="filters-input"
                      name="max"
                      id="max-value"
                      type="number"
                      onChange={handleChangeFiltro}
                    ></input>
                  </div>
                  <div className="btn-container">
                    <button className="btn-clear-filters" type="submit">Limpar Filtros</button>
                    <button className="btn-apply-filters" type="submit">Aplicar Filtros</button>
                  </div>
                </form>
              </div>
            )
          }

          <table className="table flex-column">
            <tr className="table-head">
              <th className="column-title" id="date">Data</th>
              <th className="column-title" id="week-day">Dia da semana</th>
              <th className="column-title">Descrição</th>
              <th className="column-title">Categoria</th>
              <th className="column-title valor" id="value" onClick={ordenarValor}><span>Valor<img src={ordemCrescente ? './assets/setaDown.svg' : './assets/setaUp.svg'} /></span></th>
              <td className="tabela__header_placeholder"><img src="./assets/editar.svg" /><img src="./assets/excluir.svg" /></td>
            </tr>
            <div className="table-body">
              {
                registros.map(registro => {
                  // const x = {...registro};
                  // const datinha = x.date;
                  return (
                    <Registro
                      date={registro.date}
                      week_day={registro.week_day}
                      description={registro.description}
                      category={registro.category}
                      value={registro.value}
                      id={registro.id}
                      registro={registro}
                      editarRegistro={editarRegistro}
                      excluirRegistro={excluirRegistro}
                    />
                  )
                })
              }
            </div>
            {/* <tr className="tabela__itens">
              <td className="tabela__item">01/09/21</td>
              <td className="tabela__item">Quarta</td>
              <td className="tabela__item">Venda de brigadeiros</td>
              <td className="tabela__item">Pix</td>
              <td className="tabela__item"> R$ 100,00</td>
              <td className="tabela__item"><img src="./assets/editar.svg" /><img src="./assets/excluir.svg" /></td>
            </tr>
            <hr width="100%"></hr> */}
          </table>
        </div>
        <div className="right-side">
          <div className="container-resume">
            <h1>Resumo</h1>
            <div>
              <span className="resumo__entrada-saida">Entradas<b className="in">R$ {(entrada / 100).toFixed(2)}</b></span>

            </div>
            <div>
              <span className="resumo__entrada-saida">Saída<b className="out">R$ {(saida / 100).toFixed(2)}</b></span>

            </div>
            <hr />
            <div>
              <span className="saldo">Saldo<b className="balance">R$ {(saldo / 100).toFixed(2)}</b></span>

            </div>
          </div>
          <button className="btn-add" onClick={() => setAddRegistroVisivel(true)}>Adicionar Registro</button>
        </div>
        {
          addRegistroVisivel && (
            <div className="position-center">
              <div className="intermediate">
                <div className="modal-container">

                  <h1>
                    Adicionar Registro
                    <img
                      className="close-icon"
                      onClick={handleCloseModal}
                      src="./assets/close.svg"
                    />
                  </h1>
                  <div className="entrada-saida">
                    <span
                      className={`${tipoSaida ? "greyish" : "colored-entrada"}`}
                      id="credit-button"
                      onClick={() => setTipoSaida(false)}
                    >
                      Entrada
                    </span>
                    <span
                      className={`${tipoSaida ? "colored-saida" : "greyish"}`}
                      id="debit-button"
                      onClick={() => setTipoSaida(true)}
                    >
                      Saída
                    </span>
                  </div>
                  <form onSubmit={handleSubmitAdicionar}>
                    <div className="input flex-column">
                      <label>Valor</label>
                      <input name="valor" value={formulario.valor} type="number" step="0.01" min="0.01" onChange={handleChange} required></input>
                    </div>
                    <div className="input flex-column">
                      <label>Categoria</label>
                      <input name="categoria" value={formulario.categoria} type="text" onChange={handleChange} required></input>
                    </div>
                    <div className="input flex-column">
                      <label>Data</label>
                      <input name="data" value={formulario.data} type="date" onChange={handleChange} required></input>
                    </div>
                    <div className="input flex-column">
                      <label>Descrição</label>
                      <input name="descricao" value={formulario.descricao} type="text" onChange={handleChange} required></input>
                    </div>
                    <button className="btn-insert" type="submit">Confirmar</button>
                  </form>
                </div>
              </div>
            </div>
          )
        }
        {
          editandoRegistro && (
            <div className="position-center">
              <div className="intermediate">
                <div className="modal-container">
                  <h1>
                    Editar Registro
                    <img
                      className="close-icon"
                      onClick={handleCloseModal}
                      src="./assets/close.svg"
                    />
                  </h1>
                  <div className="entrada-saida">
                    <span
                      className={`${tipoSaida ? "greyish" : "colored-entrada"}`}
                      id="credit-button"
                      onClick={() => setTipoSaida(false)}
                    >
                      Entrada
                    </span>
                    <span
                      className={`${tipoSaida ? "colored-saida" : "greyish"}`}
                      id="debit-button"
                      onClick={() => setTipoSaida(true)}
                    >
                      Saída
                    </span>
                  </div>
                  <form onSubmit={handleSubmitEditar}>
                    <div className="input flex-column">
                      <label>Valor</label>
                      <input name="valor" value={formulario.valor} type="number" step="0.01" min="0.01" onChange={handleChange}></input>
                    </div>
                    <div className="input flex-column">
                      <label>Categoria</label>
                      <input name="categoria" value={formulario.categoria} type="text" onChange={handleChange}></input>
                    </div>
                    <div className="input flex-column">
                      <label>Data</label>
                      <input name="data" value={formulario.data} type="date" onChange={handleChange}></input>
                    </div>
                    <div className="input flex-column">
                      <label>Descrição</label>
                      <input name="descricao" value={formulario.descricao} type="text" onChange={handleChange}></input>
                    </div>
                    <button className="btn-insert" type="submit">Confirmar</button>
                  </form>
                </div>
              </div>
            </div>
          )
        }
      </main>
    </div>
  );
}

export default App;
