import './style.css';
import { useState, useEffect } from 'react';

export function Registro({ id, category, date, description, value, type, week_day, registro, excluirRegistro, editarRegistro, setDeletar, setRegistros }) {
  const [visivel, setVisivel] = useState(false);

  useEffect(() => {
    const array = [...registro];
    array.forEach(x => {
      if (x.id === id) {
        if (x.deletar) {
          setVisivel(true);
        } else {
          return "hidden"
        }
      }
    })
  }, [registro]);



  return (
    <div>
      <tr key={id} className="table-line">
        <td className="line-items">
          {new Date(date).toLocaleDateString('pt-BR', { timeZone: 'UTC' })}
        </td>
        <td className="line-items">{week_day}</td>
        <td className="line-items" id="description">{description}</td>
        <td className="line-items">{category}</td>
        <td className={`line-items value ${type === "debit" ? "debit-color" : "credit-color"}`}>
          {`${(value / 100).toLocaleString("pt-BR", {
            style: 'currency',
            currency: 'BRL'
          })}`}
        </td>
        <td className="line-items">
          <img
            className="edit-icon"
            onClick={() => { editarRegistro(registro) }}
            src="./assets/editar.svg" />
          <img
            className="delete-icon"
            // onClick={() => { excluirRegistro(id) }}
            onClick={() => setRegistros(prevState => {
              const array = [...prevState];
              let registroSelecionado = array.find(x => x.id === id);
              const index = array.findIndex(x => x.id === id);
              registroSelecionado = { ...registroSelecionado, deletar: true }
              array.splice(index, 1, registroSelecionado);
              return array;
            })}
            src="./assets/excluir.svg" />
        </td>
      </tr>
      <hr width="100%"></hr>
      <div className={`container-confirm-delete ${visivel ? "" : "hidden"}`}>
        <h1>Apagar item?</h1>
        <button className="btn-actions-confirm" onClick={() => { excluirRegistro(id) }}>Sim</button>
        <button className="btn-actions-delete" onClick={() => setDeletar(false)}>NÃ£o</button>
      </div>
    </div>

  )
};

