import './App.css';
import { listarRegistros } from './registros'
import { useState, useEffect } from 'react';

function Registro({ id, category, date, description, value, week_day, excluirRegistro, editarRegistro }) {
  return (
    <div>
      <tr key={id} className="tabela__itens">
        <td className="tabela__item">{date}</td>
