import './style.css';


export function Chip({setFiltro, item}) {
    const [chipAtivo, setChipAtivo] = useState(false);

    return (
        <div
            className="container-chip"
            onClick={() => setFiltro(prevState => {
                const array = [...prevState, item];
                return array;
            })}
            key={item}>
            <span className="chip-span">{item}</span>
            <img className="chip-icon" src="./assets/plus-icon.svg" />
        </div>
    )
}
