import './App.css';
import { listarRegistros } from './registros'
import { useState, useEffect } from 'react';

function Registro({ category, date, description, value, week_day }) {
  return (
    <div>
      <tbody>
      <tr className="tabela__itens">
        <td className="tabela__item">{date}</td>
        <td className="tabela__item">{week_day}</td>
        <td className="tabela__item">{description}</td>
        <td className="tabela__item">{category}</td>
        <td className="tabela__item">{value}</td>
        <td className="tabela__item">
          <img src="./assets/editar.svg" alt="" />
          <img src="./assets/excluir.svg" alt="" />
        </td>
      </tr>

      </tbody>
      <hr width="100%"></hr>
    </div>

  )
}

function App() {
  const [addRegistroVisivel, setAddRegistroVisivel] = useState(false);
  const [filtroVisivel, setFiltroVisivel] = useState(false);
  const [formulario, setFormulario] = useState({ valor: 0, categoria: '', data: '', descricao: '' });
  const [saida, setSaida] = useState(true);
  const [registros, setRegistros] = useState([]);

  useEffect(() => {
    listarRegistros().then((response) => {
      setRegistros(response);
      console.log(response)
    })
    // async function importarRegistros() {
    //   const response = await fetch('http://localhost:3333/transactions');
    //   const data = await response.json();
    //   setRegistros(data);
    // }
    // importarRegistros();
  }, []);

  // const registros = listarRegistros();
  // promise => listarRegistros é função async, logo exige await (n pode pq é variavel) ou then. Porém, ao usar then
  // Requisição => ou dentro de useEffect ou em função handle. Nunca "jogado" (como na linha 41), pq, se o componente renderizar, a req será executada de novo.

  function handleSubmit(event) {
    event.preventDefault();
    // checar event.target, map
    const { valor, categoria, data, descricao } = formulario;
    const form = {
      "date": data,
      "week_day": '',
      "description": descricao,
      "value": valor,
      "category": categoria,
      "type": saida ? "debit" : "credit"
    };

    fetch("http://localhost:3333/transactions", {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify(form)
    });

    setAddRegistroVisivel(false);
    // listarRegistros();
  }

  function handleChange(event) {
    setFormulario({ ...formulario, [event.target.name]: event.target.value })
  }

  return (
    <div className="App">
      <header>
        <img src="./assets/Logo.svg" alt="" />
      </header>
      <main>
        <div className="left-side flex-column">
          <button onClick={() => !filtroVisivel ? setFiltroVisivel(true) : setFiltroVisivel(false)} className="filtrar-Btn">
            <span className="filtrar-Btn__span">
              <img src="./assets/filtro.svg" alt="" />
              Filtrar
            </span>
          </button>

          {
            filtroVisivel && (
              <div className="filtro">

              </div>
            )
          }

          <table className="tabela flex-column">
            <tbody>
              <tr className="tabela__header">
                <th className="tabela__header_item">Data</th>
                <th className="tabela__header_item">Dia da semana</th>
                <th className="tabela__header_item">Descrição</th>
                <th className="tabela__header_item">Categoria</th>
                <th className="tabela__header_item valor">Valor</th>
                <td className="tabela__header_placeholder"><img src="./assets/editar.svg" alt="" /><img src="./assets/excluir.svg" alt="" /></td>
              </tr>
            </tbody>

            {
              registros.map(registro => {
                return (
                  <Registro
                    date={registro.date}
                    week_day={registro.week_day}
                    description={registro.description}
                    category={registro.category}
                    value={registro.value}
                  />
                )
              })
            }
            {/* <Registro /> */}
            {/* <tr className="tabela__itens">
              <td className="tabela__item">01/09/21</td>
              <td className="tabela__item">Quarta</td>
              <td className="tabela__item">Venda de brigadeiros</td>
              <td className="tabela__item">Pix</td>
              <td className="tabela__item"> R$ 100,00</td>
              <td className="tabela__item"><img src="./assets/editar.svg" /><img src="./assets/excluir.svg" /></td>
            </tr>
            <hr width="100%"></hr> */}
          </table>
        </div>
        <div className="right-side">
          <div className="resumo">
            <h1>Resumo</h1>
            <div>
              <span className="resumo__entrada-saida">Entradas<b className="resumo__entrada-value">R$200,00</b></span>

            </div>
            <div>
              <span className="resumo__entrada-saida">Saída<b>R$70,50</b></span>

            </div>
            <hr />
            <div>
              <span className="saldo">Saldo<b>R$200,00</b></span>

            </div>
          </div>
          <button className="addRegistro-Btn" onClick={() => setAddRegistroVisivel(true)}>Adicionar Registro</button>
        </div>
        {
          addRegistroVisivel && (
            <div className="modal-container">
              <div className="modal_add-registro flex-column">
                <h1>
                  Adicionar Registro
                  <img
                    className="closeImg"
                    onClick={() => setAddRegistroVisivel(false)}
                    src="./assets/close.svg"
                    alt=""
                  />
                </h1>
                <div className="entrada-saida">
                  <span
                    className={`entrada ${saida ? "greyish" : "colored-entrada"}`}
                    onClick={() => setSaida(false)}
                  >
                    Entrada
                  </span>
                  <span
                    className={`saida ${saida ? "colored-saida" : "greyish"}`}
                    onClick={() => setSaida(true)}
                  >
                    Saída
                  </span>
                </div>
                <form onSubmit={handleSubmit}>
                  <div className="input flex-column">
                    <label>Valor</label>
                    <input name="valor" type="number" step="0.01" min="0.01" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Categoria</label>
                    <input value="" name="categoria" type="text" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Data</label>
                    <input name="data" type="date" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Descrição</label>
                    <input name="descricao" type="text" onChange={handleChange}></input>
                  </div>
                  <button className="confirmar-Btn" type="submit">Confirmar</button>
                </form>
              </div>
            </div>
          )
        }
      </main>
    </div>
  );
}

export default App;
