import './App.css';
import { listarRegistros } from './registros'
import { useState, useEffect } from 'react';
import { use } from 'chai';

function Registro({ id, category, date, description, value, week_day, registro, excluirRegistro, editarRegistro }) {
  return (
    <div>
      <tr key={id} className="tabela__itens">
        <td className="tabela__item">{date}</td>
        <td className="tabela__item">{week_day}</td>
        <td className="tabela__item">{description}</td>
        <td className="tabela__item">{category}</td>
        <td className="tabela__item">{value}</td>
        <td className="tabela__item">
          <img onClick={() => { editarRegistro(registro) }} src="./assets/editar.svg" />
          <img onClick={() => { excluirRegistro(id) }} src="./assets/excluir.svg" />
        </td>
      </tr>
      <hr width="100%"></hr>
    </div>

  )
}

function App() {
  const [addRegistroVisivel, setAddRegistroVisivel] = useState(false);
  const [filtroVisivel, setFiltroVisivel] = useState(false);
  const [formulario, setFormulario] = useState({ valor: 0, categoria: '', data: '', descricao: '' });
  const [tipoSaida, setTipoSaida] = useState(true);
  const [registros, setRegistros] = useState([]);
  const [editandoRegistro, setEditandoRegistro] = useState(false);
  const [entrada, setEntrada] = useState(0)
  const [saida, setSaida] = useState(0)
  const [saldo, setSaldo] = useState(0)
  const [ordemCrescente, setOrdemCrescente] = useState(true);
  const [minMax, setMinMax] = useState({min: 0, max: 0});

  async function handleFiltro(event) {
    event.preventDefault();

  }

  useEffect(() => {
    listarRegistros().then((response) => {
      setRegistros(response);
    })
  }, []);
  // console.log(registros)

  useEffect(() => {
    const arrayDeRegistros = [...registros];

    let somaEntrada = 0, somaSaida = 0, valorSaldo = 0;

    for (let i = 0; i < arrayDeRegistros.length; i++) {
      if (arrayDeRegistros[i].type === "credit") {
        somaEntrada = somaEntrada + Number(arrayDeRegistros[i].value)
        // console.log("entrada")
      } else if (arrayDeRegistros[i].type === "debit") {
        somaSaida = somaSaida + Number(arrayDeRegistros[i].value);
        // console.log("saida")
      }
    };

    valorSaldo = somaEntrada - somaSaida;

    setEntrada(somaEntrada);
    setSaida(somaSaida);
    setSaldo(valorSaldo);
    // console.log(saida, entrada, saldo)
  }, [[], registros])

  // FUNÇÃO ENVIO DE FORMULÁRIO
  function handleSubmit(event) {
    event.preventDefault();
    // checar event.target, map
    const { valor, categoria, data, descricao } = formulario;
    const form = {
      "date": data,
      "week_day": '',
      "description": descricao,
      "value": valor,
      "category": categoria,
      "type": tipoSaida ? "debit" : "credit"
    };

    fetch("http://localhost:3333/transactions", {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify(form)
    }).then(response => {
      const responseJSON = response.json().then(novoRegistro => {
        setRegistros([...registros, novoRegistro]);
      })
    })
    setAddRegistroVisivel(false);
    setTipoSaida(true);
  }

  // FUNÇÃO INPUT
  function handleChange(event) {
    setFormulario({ ...formulario, [event.target.name]: event.target.value })
    setMinMax({...minMax, e})
  }

  async function excluirRegistro(id) {
    const deletado = await fetch(`http://localhost:3333/transactions/${id}`, {
      method: "DELETE",
    });
    console.log("delete", deletado);
    setRegistros((prevState) => {
      const novaLista = [...prevState];
      const index = novaLista.findIndex(x => x.id = id);
      novaLista.splice(index, 1);
      return novaLista;
    })
  };

  async function editarRegistro(registro) {
    setEditandoRegistro(registro);
    setFormulario({
      valor: registro.value,
      categoria: registro.category,
      data: registro.date,
      descricao: registro.description,
      id: registro.id
    })
  }

  function handleSubmitEditar(event) {
    console.log("oi")
    event.preventDefault();
    // const { valor, categoria, data, descricao } = formulario;
    const body = {
      "date": formulario.data,
      "week_day": '',
      "description": formulario.descricao,
      "value": formulario.valor,
      "category": formulario.categoria,
      "type": tipoSaida ? "debit" : "credit"
    };

    fetch(`http://localhost:3333/transactions/${formulario.id}`, {
      headers: {
        'Content-Type': 'application/json'
      },
      method: "PUT",
      body: JSON.stringify(body)
    }).then(response => {
      const responseJSON = response.json().then(novoRegistro => {
        console.log(novoRegistro)
        const array = [...registros];
        let index = array.findIndex(item => item.id = formulario.id);
        array.splice(index, 1, novoRegistro);
        // console.log("array", array)
        setRegistros(array);
      })
    })
    setEditandoRegistro(false);
    setTipoSaida(true);
  }

  function handleCloseModal() {
    setTipoSaida(true);
    setAddRegistroVisivel(false);
    setEditandoRegistro(false);
  }

  function ordenarValor() {
    setRegistros(prevState => {
      const array = [...prevState];
      const arrayOrdenado = array.sort((a, b) => {
        if (ordemCrescente) {
          return Number(a.value) - Number(b.value);
        } else {
          return Number(b.value) - Number(a.value);
        }
      });
      console.log(arrayOrdenado);
      return arrayOrdenado;
    })
    setOrdemCrescente(prevState => {
      if (ordemCrescente) {
        return false;
      } else {
        return true;
      }
    });
  }

  // RENDERIZA COMPONENTE APP
  return (
    <div className="App">
      <header>
        <img src="./assets/Logo.svg" />
      </header>
      <main>
        <div className="left-side flex-column">
          <button onClick={() => !filtroVisivel ? setFiltroVisivel(true) : setFiltroVisivel(false)} className="filtrar-Btn">
            <span className="filtrar-Btn__span">
              <img src="./assets/filtro.svg" />
              Filtrar
            </span>
          </button>

          {
            filtroVisivel && (
              <form onSubmit={handleFiltro} className="filtro">
                <div className="input-filtro">
                  <label>Min</label>
                  <input name="min" type="number"></input>
                </div>
                <div className="input-filtro">
                  <label>Max</label>
                  <input name="max" type="number"></input>
                </div>
              </form>
            )
          }

          <table className="tabela flex-column">
            <tr className="tabela__header">
              <th className="tabela__header_item">Data</th>
              <th className="tabela__header_item">Dia da semana</th>
              <th className="tabela__header_item">Descrição</th>
              <th className="tabela__header_item">Categoria</th>
              <th className="tabela__header_item valor" onClick={ordenarValor}>Valor</th>
              <td className="tabela__header_placeholder"><img src="./assets/editar.svg" /><img src="./assets/excluir.svg" /></td>
            </tr>

            {
              registros.map(registro => {
                return (
                  <Registro
                    date={registro.date}
                    week_day={registro.week_day}
                    description={registro.description}
                    category={registro.category}
                    value={registro.value}
                    id={registro.id}
                    registro={registro}
                    editarRegistro={editarRegistro}
                    excluirRegistro={excluirRegistro}
                  />
                )
              })
            }
            {/* <tr className="tabela__itens">
              <td className="tabela__item">01/09/21</td>
              <td className="tabela__item">Quarta</td>
              <td className="tabela__item">Venda de brigadeiros</td>
              <td className="tabela__item">Pix</td>
              <td className="tabela__item"> R$ 100,00</td>
              <td className="tabela__item"><img src="./assets/editar.svg" /><img src="./assets/excluir.svg" /></td>
            </tr>
            <hr width="100%"></hr> */}
          </table>
        </div>
        <div className="right-side">
          <div className="resumo">
            <h1>Resumo</h1>
            <div>
              <span className="resumo__entrada-saida">Entradas<b className="resumo__entrada-value">R$ {entrada}</b></span>

            </div>
            <div>
              <span className="resumo__entrada-saida">Saída<b>R$ {saida}</b></span>

            </div>
            <hr />
            <div>
              <span className="saldo">Saldo<b>R$ {saldo}</b></span>

            </div>
          </div>
          <button className="addRegistro-Btn" onClick={() => setAddRegistroVisivel(true)}>Adicionar Registro</button>
        </div>
        {
          addRegistroVisivel && (
            <div className="modal-container">
              <div className="modal_add-registro flex-column">
                <h1>
                  Adicionar Registro
                  <img
                    className="closeImg"
                    onClick={handleCloseModal}
                    src="./assets/close.svg"
                  />
                </h1>
                <div className="entrada-saida">
                  <span
                    className={`entrada ${tipoSaida ? "greyish" : "colored-entrada"}`}
                    onClick={() => setTipoSaida(false)}
                  >
                    Entrada
                  </span>
                  <span
                    className={`saida ${tipoSaida ? "colored-saida" : "greyish"}`}
                    onClick={() => setTipoSaida(true)}
                  >
                    Saída
                  </span>
                </div>
                <form onSubmit={handleSubmit}>
                  <div className="input flex-column">
                    <label>Valor</label>
                    <input name="valor" value={formulario.valor} type="number" step="0.01" min="0.01" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Categoria</label>
                    <input name="categoria" value={formulario.categoria} type="text" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Data</label>
                    <input name="data" value={formulario.data} type="date" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Descrição</label>
                    <input name="descricao" value={formulario.descricao} type="text" onChange={handleChange}></input>
                  </div>
                  <button className="confirmar-Btn" type="submit">Confirmar</button>
                </form>
              </div>
            </div>
          )
        }
        {
          editandoRegistro && (
            <div className="modal-container">
              <div className="modal_add-registro flex-column">
                <h1>
                  Editar Registro
                  <img
                    className="closeImg"
                    onClick={handleCloseModal}
                    src="./assets/close.svg"
                  />
                </h1>
                <div className="entrada-saida">
                  <span
                    className={`entrada ${tipoSaida ? "greyish" : "colored-entrada"}`}
                    onClick={() => setTipoSaida(false)}
                  >
                    Entrada
                  </span>
                  <span
                    className={`saida ${tipoSaida ? "colored-saida" : "greyish"}`}
                    onClick={() => setTipoSaida(true)}
                  >
                    Saída
                  </span>
                </div>
                <form onSubmit={handleSubmitEditar}>
                  <div className="input flex-column">
                    <label>Valor</label>
                    <input name="valor" value={formulario.valor} type="number" step="0.01" min="0.01" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Categoria</label>
                    <input name="categoria" value={formulario.categoria} type="text" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Data</label>
                    <input name="data" value={formulario.data} type="date" onChange={handleChange}></input>
                  </div>
                  <div className="input flex-column">
                    <label>Descrição</label>
                    <input name="descricao" value={formulario.descricao} type="text" onChange={handleChange}></input>
                  </div>
                  <button className="confirmar-Btn" type="submit">Confirmar</button>
                </form>
              </div>
            </div>
          )
        }
      </main>
    </div>
  );
}

export default App;
